var open = !1;
document.getElementsByClassName("menu-toggle")[0].addEventListener("click", function () {
   open ? closeMenu() : openMenu()
});
let menuItems = document.querySelectorAll("header .menu__link");

function openMenu() {
   var mm;
   document.getElementsByClassName("mobile-menu")[0].classList.add("open"), open = !0
}

function closeMenu() {
   var mm;
   document.getElementsByClassName("mobile-menu")[0].classList.remove("open"), open = !1
}
menuItems.forEach(function (elem) {
   elem.addEventListener("click", function () {
      closeMenu()
   })
});